<?phpclass CustomerInfoViewModel extends ViewModel {    protected $viewFields;    public function _initialize(){        $this->viewFields = array(            'customer'=>array('*','_type'=>'LEFT'),            'user'=>array('full_name'=>'user_name', '_on'=>'customer.creator_role_id=user.role_id','_type'=>'LEFT'),            'role'=>array('position_id','_on'=>'customer.creator_role_id=role.role_id','_type'=>'LEFT'),            'position'=>array('department_id','_on'=>'position.position_id=role.position_id','_type'=>'LEFT'),            'role_department'=>array('name'=>'department_name','_on'=>'role_department.department_id=position.department_id')        );    }}